mapper.select:
select

from t_orderbook_stream

    order_book_id > (
        select last_id from (
            select '-' id, '-' last_id union all
            select id, last_id from t_liquid_archive_record where the_table_name='t_orderbook_stream' order by id desc limit 1
        ) as a
    ) and created_at < current_timestamp(3) limit #{size,jdbcType=INTEGER};
